'use strict';

var MemoryStore = require('../backend-request/memstore.js');
var logoutProvider = require('../middleware/logout-provider');

exports.getMemoryStore = function (routerConfig) {
  var memoryStore = new MemoryStore({
    timeout: routerConfig.sessionTimeout
  });

  memoryStore.on('timeout', function (session) {
    logoutProvider.sessionTimeoutLogout(session, routerConfig);
  });
  return memoryStore;
};
