'use strict';

var fs = require('fs');

module.exports = {
  isFile: function(pathToFile) {
    try {
      return fs.statSync(pathToFile).isFile();
    } catch (error) {
      return false;
    }
  },

  isDirectory: function(path) {
    try {
      return fs.lstatSync(path).isDirectory();
    } catch (error) {
      return false;
    }
  }
};
